mapper.select:
SELECT aidx, aordercode, aproductcode, aproduct, acustomer, ahp, date, deliveryname, dcode, bapprove, dapprove, date, stracking, sqrimg, sqrurl, shipstate FROM shipping WHERE dcode != 'N' AND bapprove != 'Y' AND dapprove != 'Y'
AND date >= concat(#{start}, ' 00:00:00') and date <= concat(#{end}, ' 23:59:59')
AND dcode = #{dcode}
ORDER BY aidx DESC LIMIT ${startno},${endno}

SELECT COUNT(*) AS ctn FROM shipping WHERE dcode != 'N' AND bapprove != 'Y' AND dapprove != 'Y'
AND date >= concat(#{start}, ' 00:00:00') and date <= concat(#{end}, ' 23:59:59')
AND dcode = #{dcode}

mapper.update:
UPDATE shipping
SET stracking=#{stracking}
WHERE aidx=#{aidx} AND stracking = 'N'

mapper.select:
SELECT stracking, aidx
FROM shipping
WHERE aidx=#{aidx}

mapper.update:
UPDATE shipping
SET sqrimg=#{sqrimg}, sqrurl=#{sqrurl}
WHERE aidx=#{aidx} AND stracking != 'N'
