mapper.select:
SELECT COUNT(*)
FROM storage
WHERE scode = #{scode}

mapper.update:
update storage
set sdeleted = 0
where scode = #{scode}

mapper.select:
SELECT acode
FROM account
WHERE acompany = #{acompany}

mapper.update:
UPDATE product
SET pdamount = pdamount - #{quantity}
WHERE pdidx = #{pdidx}

UPDATE product
SET pdamount = pdamount + #{quantity}
WHERE pdcode = #{pdcode} AND scode = #{instorecode} AND pname = #{pname}

