mapper.select:
SELECT COUNT(*)
FROM storage
WHERE scode = #{scode}

        select *
        from storage
        where sspot = #{mspot}	

		LIMIT #{startno}, #{endno}

mapper.update:
update storage
set sdeleted = 0
where scode = #{scode}

mapper.select:
SELECT acode
FROM account
WHERE acompany = #{acompany}

mapper.update:
UPDATE product
SET pdamount = pdamount - #{quantity}
WHERE pdidx = #{pdidx}

UPDATE product
SET pdamount = pdamount + #{quantity}
WHERE pdcode = #{pdcode} AND scode = #{instorecode} AND pname = #{pname}

mapper.select:
SELECT acompany, acode, pdname, pdcode, pname, pcode
FROM product
WHERE pdidx = #{pdidx}

mapper.update:
UPDATE product
SET pdamount = pdamount + #{quantity}
WHERE pdcode = #{pdcode} AND scode = #{instorecode} AND pname = #{instorepalette};

mapper.select:
select pcode
from palette
where pname = #{pname}
