mapper.select:
select aidx, aordercode, aproduct, aproductcode, acustomer, ahp, addr, date, adeliveryck, shipstate from shipping
where shipstate != '배송완료' AND dapprove = 'N' 

AND adeliveryck = #{radio}


AND date BETWEEN #{startDate} AND #{endDate}

order by date desc

select dname, dspot, didx from delivery
where dspot = #{mspot}
and dimgck = "Y"

mapper.update:
	UPDATE shipping
    SET adeliveryck = "Y", deliveryname = #{dname}, dcode = #{didx}, dspot = #{dspot}  
    WHERE aidx = #{aidx}
